import Button from "./Button";

export default function Question(props) {
    const {question, onOptionChoose} = props;
    const options = [];
    question.incorrect_answers.map((ans) => {
        options.push(ans);
    })
    options.push(question.correct_answer);

    function handleCurOptionChoose(event) {
        const curOption = event.target.value;
        onOptionChoose(curOption === question.correct_answer);
    }

    return <>
        <div>Correct Answers: 0/0</div>
        <h2>Question</h2>
        <div className="options">
            {options.map((option, index) => {
                return <Button key={index} value={option} onButtonClick={handleCurOptionChoose}>{option}</Button>
            })}
        </div>
        <button onClick={() => onOptionChoose(0)}>Next Question</button>
    </>
}
